$repeated_words_arr = explode(" ", $data);
	
	function process_repeated_words($repeated_words_arr,&$repeated_words){
		foreach ($repeated_words_arr as $data_value) {
			
			if( in_array($data_value,array_keys($repeated_words)) ){
				$repeated_words[$data_value]['number'] = $repeated_words[$data_value]['number'] + 1;
			}else{
				$repeated_words[$data_value] = ['number' => 1];
			}
	
		}	
	}

	function clear_process_repeated_words($repeated_words,&$clear_repeated_words){
		foreach ($repeated_words as $repeated_words_key => $repeated_words_value) {
			$clear_repeated_words.=$repeated_words_key.$repeated_words_value['number'];
		}
	}

	function get_repeated_words($repeated_words_arr){
		$repeated_words = [];
		$clear_repeated_words = '';

		process_repeated_words($repeated_words_arr,$repeated_words);
		clear_process_repeated_words($repeated_words,$clear_repeated_words);

		return $clear_repeated_words;
	}
	
